import React, { useState, useEffect } from 'react';

function Contact() {
    const [name, setName] = useState('');
    const [email, setEmail] = useState('');
    const [message, setMessage] = useState('');
    const [rating, setRating] = useState(0);
    const [selectedGif, setSelectedGif] = useState(null);

    // Array of random GIF URLs (Use your own fun GIF URLs here)
    const gifArray = [
        'https://media.giphy.com/media/f9oqmJ4iaPOmY/giphy.gif',
        'https://media.giphy.com/media/3o7aCTfyhYawdOXcFW/giphy.gif',
        'https://media.giphy.com/media/l4FGuhL4U2WyjdkaY/giphy.gif',
        'https://media.giphy.com/media/1Z02vuppxP1Pa/giphy.gif',
        'https://media.giphy.com/media/26FPqvKlv3KCPUSfu/giphy.gif'
    ];

    const ratingEmojis = {
        1: 'ðŸ˜”',
        2: 'ðŸ˜•',
        3: 'ðŸ™‚',
        4: 'ðŸ˜ƒ',
        5: 'ðŸ¤©'
    };

    const handleRatingClick = (star) => {
        setRating(star);
        // Randomly pick a GIF from the array
        const randomIndex = Math.floor(Math.random() * gifArray.length);
        setSelectedGif(gifArray[randomIndex]);
    };

    const handleSubmit = (e) => {
        e.preventDefault();
        const subject = encodeURIComponent("Feedback on Your Website");
        const body = encodeURIComponent(
            `Name: ${name}\nEmail: ${email}\nRating: ${rating} Stars\nMessage:\n${message}`
        );
        const mailtoLink = `mailto:yourEmail@example.com?subject=${subject}&body=${body}`;
        window.location.href = mailtoLink; 
    };

    return (
        <div className="page-container">
            <h2 className="page-title">Contact Me</h2>
            <div className="card">
                <h3>Rate My Website</h3>
                <div className="star-rating">
                    {[1,2,3,4,5].map(star => (
                        <span 
                            key={star} 
                            className={`star ${star <= rating ? 'filled' : ''}`} 
                            onClick={() => handleRatingClick(star)}
                            role="button"
                            aria-label={`Rate ${star} stars`}
                        >
                            â˜…
                        </span>
                    ))}
                </div>
                {rating > 0 && (
                    <div className="rating-feedback">
                        <div className="rating-emoji">
                            {ratingEmojis[rating]}
                        </div>
                        {selectedGif && (
                            <div className="rating-gif">
                                <img src={selectedGif} alt="Random GIF" />
                            </div>
                        )}
                    </div>
                )}
            </div>

            <div className="card">
                <h3>Get in Touch</h3>
                <form onSubmit={handleSubmit} className="contact-form">
                    <div className="form-group">
                        <label htmlFor="name">Name</label>
                        <input 
                            type="text" 
                            id="name" 
                            value={name} 
                            onChange={(e) => setName(e.target.value)} 
                            required 
                        />
                    </div>
                    <div className="form-group">
                        <label htmlFor="email">Email</label>
                        <input 
                            type="email" 
                            id="email"
                            value={email} 
                            onChange={(e) => setEmail(e.target.value)} 
                            required 
                        />
                    </div>
                    <div className="form-group">
                        <label htmlFor="message">Message</label>
                        <textarea 
                            id="message" 
                            rows="5" 
                            value={message} 
                            onChange={(e) => setMessage(e.target.value)} 
                            required
                        ></textarea>
                    </div>
                    <button type="submit" className="send-btn">Send Email</button>
                </form>
            </div>
        </div>
    );
}

export default Contact;
