import React, { useEffect, useState } from 'react';
import './App.css';

function App() {
    const [bar1Width, setBar1Width] = useState(0);
    const [bar2Width, setBar2Width] = useState(0);

    useEffect(() => {
        const interval1 = setInterval(() => {
            setBar1Width((prev) => {
                if (prev >= 70) return prev;
                return prev + 1;
            });
        }, 30);

        const interval2 = setInterval(() => {
            setBar2Width((prev) => {
                if (prev >= 50) return prev;
                return prev + 1;
            });
        }, 30);

        return () => {
            clearInterval(interval1);
            clearInterval(interval2);
        };
    }, []);

    return (
        <div className="App">
            <header className="App-header">
                <h1>Grant Dale Hortelano</h1>
                <p>Advocating for Social Justice: Tackling Poverty and Homelessness</p>
            </header>
            <nav>
                <a href="#journey">My Journey</a>
                <a href="#poverty">Understanding Poverty</a>
                <a href="#faith">Living Out My Faith</a>
                <a href="#references">References</a>
            </nav>
            <main className="container">
                <section id="journey">
                    <h2>Who Have I Become?</h2>
                    <p>Hello, I’m Grant Dale Hortelano. Over the years, my journey in the Catholic education system has helped me grow into a person who values compassion, empathy, and social justice. Today, I aim to make a positive impact by addressing the issue of poverty and homelessness.</p>
                </section>
                <section id="poverty" className="social-justice">
                    <h2>Poverty and Homelessness: A Call for Justice</h2>
                    <div>
                        <h3>Global Perspective</h3>
                        <div className="bar-chart">
                            <div className="bar" style={{ width: `${bar1Width}%` }}>
                                <span className="label">{bar1Width}% - Global Housing Crisis</span>
                            </div>
                            <div className="bar" style={{ width: `${bar2Width}%` }}>
                                <span className="label">{bar2Width}% - Urban Slum Population</span>
                            </div>
                        </div>
                    </div>
                </section>
                <section id="faith">
                    <h2>Ontario Catholic Graduate Expectations</h2>
                    <div>
                        <h3>Achievements</h3>
                        <p>"I’ve demonstrated care and compassion by volunteering at the Goodnight Program."</p>
                    </div>
                </section>
                <section id="references">
                    <h2>Bibliography</h2>
                    <ul>
                        <li>Vatican.va. Catechism of the Catholic Church. 2024.</li>
                    </ul>
                </section>
            </main>
            <footer>
                <p>&copy; 2024 Grant Dale Hortelano. All Rights Reserved.</p>
            </footer>
        </div>
    );
}

export default App;
