import React, { useEffect, useState } from 'react';

function BarChart({ data, duration }) {
    const [widths, setWidths] = useState(data.map(() => 0));

    useEffect(() => {
        let startTime = null;

        const animate = (timestamp) => {
            if (!startTime) startTime = timestamp;
            const elapsed = timestamp - startTime;

            const updatedWidths = data.map((item, index) => {
                const progress = Math.min((elapsed / duration) * item.target, item.target);
                return progress;
            });

            setWidths(updatedWidths);

            const stillAnimating = updatedWidths.some((val, idx) => val < data[idx].target);
            if (stillAnimating) {
                requestAnimationFrame(animate);
            }
        };

        requestAnimationFrame(animate);
    }, [data, duration]);

    return (
        <div className="modern-bar-chart">
            {data.map((item, index) => (
                <div className="bar-container" key={index}>
                    <div className="bar-label">
                        {Math.round(widths[index])}% - {item.label}
                    </div>
                    <div className="bar-background">
                        <div 
                            className="bar-fill" 
                            style={{ width: `${widths[index]}%` }}
                        ></div>
                    </div>
                </div>
            ))}
        </div>
    );
}

export default BarChart;
