import React, { useRef } from 'react';
import { BrowserRouter as Router, Routes, Route, Link, useLocation } from 'react-router-dom';
import { CSSTransition, TransitionGroup } from 'react-transition-group';
import Home from './Home';
import Poverty from './Poverty';
import Faith from './Faith';
import References from './References';
import './App.css';

function AnimatedRoutes() {
  const location = useLocation();
  const nodeRef = useRef(null);

  return (
    <TransitionGroup component={null}>
      <CSSTransition
        key={location.pathname}
        classNames="page"
        timeout={300}
        nodeRef={nodeRef}
      >
        {/* Wrap the Routes in a div and attach the ref */}
        <div ref={nodeRef}>
          <Routes location={location}>
            <Route path="/" element={<Home />} />
            <Route path="/poverty" element={<Poverty />} />
            <Route path="/faith" element={<Faith />} />
            <Route path="/references" element={<References />} />
          </Routes>
        </div>
      </CSSTransition>
    </TransitionGroup>
  );
}

function App() {
  return (
    <Router>
      <div className="App">
        <header className="main-header">
          <div className="logo">G. Hortelano</div>
          <nav className="main-nav">
            <Link to="/">My Journey</Link>
            <Link to="/poverty">Understanding Poverty</Link>
            <Link to="/faith">Living Out My Faith</Link>
            <Link to="/references">References</Link>
          </nav>
        </header>

        <main className="main-content">
          <AnimatedRoutes />
        </main>

        <footer className="footer">
          <p>Â© 2024 Grant Dale Hortelano. All Rights Reserved.</p>
        </footer>
      </div>
    </Router>
  );
}

export default App;
